void dooro ()
{
	nMotorEncoder[mDoor]=0;
	while(nMotorEncoder[mDoor]<95)
	{
		motor[mDoor]=70;
		sleep(1);
	}

}
void doorc ()
{
	nMotorEncoder[mDoor]=0;
	while(nMotorEncoder[mDoor]>-95)
	{
		motor[mDoor]=-70;
		sleep(1);
	}
}
task main()
{
	nMotorEncoder[mOut]=0;
	while (nMotorEncoder[mOut]>-2500)
	{
		motor[mOut]= -100;

		wait1Msec(1);
	}
	motor[mOut]=0;
	dooro ();

	doorc();

		nMotorEncoder[mOut]=0;
	while (nMotorEncoder[mOut]<2500)
	{
		motor[mOut]=  100;

		wait1Msec(1);
	}

}
